import React, { Fragment, useState } from "react";

const EditTodo = ({ todo }) => {
    const [description, setDescription] = useState(todo.description)

    // edit description

    const updateDescription = async (e) => {
        e.preventDefault();
        try {
          const body = {description};
          const response = await fetch(`http://localhost:5000/todos/${todo.id}`, 
          {
              method: "PUT",
              headers: {"Content-Type": "application/json"},
              body: JSON.stringify(body)
          })
          window.location = "/";
        } catch (err) {
            console.error(err.message)
        }
    }

    return (
        <Fragment>
            {/* <-- Button to Open the Modal --> */}
<button type="button" class="btn btn-info" data-toggle="modal" data-target={`#id${todo.id}`}>
  Edit
</button>

{/* <-- The Modal --> */}
<div class="modal fade " id={`id${todo.id}`}>
  <div class="modal-dialog">
    <div class="modal-content">

      {/* <!-- Modal Header --> */}
      <div class="modal-header">
        <h4 class="modal-title">Edit To-Do</h4>
        <button type="button" class="close" data-dismiss="modal"
        onClick={() => setDescription}
        >&times;</button>
      </div>

      {/* <-- Modal body --> */}
      <div class="modal-body">
        <input type="text" className="form-control" value={description} onChange={e => setDescription(e.target.value)} />
      </div>

      {/* <-- Modal footer --> */}
      <div class="modal-footer">
        <button type="button" class="btn btn-info" data-dismiss="modal" onClick={e => updateDescription(e)}>Edit</button>
        <button type="button" class="btn btn-danger" data-dismiss="modal">Close</button>
      </div>

    </div>
  </div>
</div>
        </Fragment>
    )
};

export default EditTodo;
